import React, { useState, useEffect } from 'react';
import Tree from 'react-d3-tree';
import { ReactMediaRecorder } from 'react-media-recorder';

function App() {
  const [notes, setNotes] = useState('ここに整形された文字起こしを表示します');
  const [summary, setSummary] = useState('ここに議事録と改善案が表示されます');
  const [mindmapData, setMindmapData] = useState(null);

  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm');

    try {
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    }
  };

  return (
    <div style={styles.appContainer}>
      <header style={styles.header}>
        <h1 style={styles.title}>会議AIツール</h1>
      </header>

      <div style={styles.contentContainer}>
        <ReactMediaRecorder
          audio
          video={false}
          mimeType="audio/webm;codecs=opus"
          onStop={(blobUrl, blob) => {
            sendAudioToServer(blob);
          }}
          render={({ status, startRecording, stopRecording }) => (
            <div style={styles.innerContainer}>
              {/* 録音ステータス */}
              <div style={styles.statusWrapper}>
                <p style={styles.statusText}>
                  録音ステータス: <span style={styles.statusValue}>{status}</span>
                </p>
              </div>

              {/* 録音ボタン */}
              <div style={styles.buttonGroup}>
                <button style={styles.recordButton} onClick={startRecording}>
                  録音開始
                </button>
                <button style={styles.stopButton} onClick={stopRecording}>
                  録音停止・議事録生成
                </button>
              </div>

              {/* AIによる議事録と改善案 */}
              <div style={styles.card}>
                <h2 style={styles.cardTitle}>AIによる議事録と改善案</h2>
                <p style={styles.cardContent}>{summary}</p>
              </div>

              {/* マインドマップ表示エリア */}
              <div style={styles.mindmapContainer}>
                {mindmapData && (
                  <Tree
                    data={mindmapData}
                    orientation="vertical"
                    translate={{ x: 500, y: 50 }}
                    pathFunc="diagonal"
                    separation={{ siblings: 1.5, nonSiblings: 2 }}
                    nodeSize={{ x: 300, y: 100 }}
                  />
                )}
              </div>

              {/* 整形された文字起こし */}
              <div style={styles.card}>
                <h2 style={styles.cardTitle}>整形された文字起こし</h2>
                <p style={styles.cardContent}>{notes}</p>
              </div>
            </div>
          )}
        />
      </div>
    </div>
  );
}

// ChatGPTっぽいダークテーマを意識したスタイル定義
const styles = {
  appContainer: {
    backgroundColor: '#343541', // ChatGPT系ダークグレー
    minHeight: '100vh',
    margin: 0,
    padding: 0,
    fontFamily: "'Noto Sans JP', sans-serif",
    display: 'flex',
    flexDirection: 'column',
  },
  header: {
    backgroundColor: '#40414F', // 上部やや明るいグレー
    padding: '16px',
    textAlign: 'center',
    borderBottom: '1px solid #565869',
  },
  title: {
    color: '#FFFFFF',
    fontSize: '1.5rem',
    margin: 0,
  },
  contentContainer: {
    flex: 1,
    display: 'flex',
    justifyContent: 'center',
    padding: '20px',
  },
  innerContainer: {
    width: '100%',
    maxWidth: '1000px',
  },
  statusWrapper: {
    textAlign: 'center',
    marginBottom: '10px',
  },
  statusText: {
    color: '#E8E8E8',
    fontSize: '1rem',
    margin: 0,
  },
  statusValue: {
    color: '#00FE93', // ChatGPTのアクセントに近い緑色
    fontWeight: 'bold',
  },
  buttonGroup: {
    display: 'flex',
    gap: '10px',
    justifyContent: 'center',
    marginBottom: '20px',
  },
  recordButton: {
    backgroundColor: '#00A67E', // ChatGPT系グリーン
    color: '#FFFFFF',
    border: 'none',
    padding: '10px 16px',
    fontSize: '1rem',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.2s',
  },
  stopButton: {
    backgroundColor: '#FF5C5C',
    color: '#FFFFFF',
    border: 'none',
    padding: '10px 16px',
    fontSize: '1rem',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.2s',
  },
  card: {
    backgroundColor: '#40414F',
    borderRadius: '8px',
    padding: '16px',
    marginBottom: '20px',
    color: '#E8E8E8',
    boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
  },
  cardTitle: {
    margin: '0 0 10px 0',
    fontSize: '1.2rem',
    color: '#FFFFFF',
    borderBottom: '1px solid #565869',
    paddingBottom: '4px',
  },
  cardContent: {
    margin: 0,
    fontSize: '1rem',
    lineHeight: '1.6',
    whiteSpace: 'pre-wrap',
  },
  mindmapContainer: {
    width: '100%',
    height: '500px',
    backgroundColor: '#2E2F3C', // 内側を少し濃いめに
    borderRadius: '8px',
    marginBottom: '20px',
    position: 'relative',
    overflow: 'hidden',
    border: '1px solid #565869',
  },
};

export default App;
