import React, { useState } from 'react';
import Tree from 'react-d3-tree';
import { ReactMediaRecorder } from 'react-media-recorder';

function App() {
  const [notes, setNotes] = useState('ここに整形された文字起こしを表示します');
  const [summary, setSummary] = useState('ここに議事録と改善案が表示されます');
  const [mindmapData, setMindmapData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);
  const [title, setTitle] = useState(''); // タイトル入力用のstate追加

  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm');

    try {
      setLoading(true);
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  const saveToDatabase = async () => {
    if (!title.trim()) {
      alert("タイトルを入力してください。");
      return;
    }

    try {
      const res = await fetch('http://localhost:8000/save-minutes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          formatted_transcript: notes,
          analysis: summary,
          mindmap: mindmapData,
          title: title // 入力されたタイトルを使用
        }),
      });

      if (!res.ok) throw new Error('保存に失敗しました');

      const result = await res.json();
      if (result.status === 'success') {
        setSaveStatus('保存が完了しました！');
      } else {
        setSaveStatus('保存に失敗しました');
      }
    } catch (err) {
      console.error(err);
      setSaveStatus('保存中にエラーが発生しました');
    }
  };

  const closeSaveModal = () => {
    setSaveStatus(null);
  };

  return (
    <div style={styles.appContainer}>
      <header style={styles.header}>
        <h1 style={styles.title}>会議AIツール</h1>
      </header>

      {loading && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={{ fontSize: '18px', color: '#333' }}>議事録を生成中です...お待ちください</p>
          </div>
        </div>
      )}

      {saveStatus && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={{ fontSize: '18px', color: '#333' }}>{saveStatus}</p>
            <button style={styles.closeButton} onClick={closeSaveModal}>閉じる</button>
          </div>
        </div>
      )}

      <div style={styles.recorderContainer}>
        <ReactMediaRecorder
          audio
          video={false}
          mimeType="audio/webm;codecs=opus"
          onStop={(blobUrl, blob) => sendAudioToServer(blob)}
          render={({ status, startRecording, stopRecording }) => (
            <div style={styles.controlsWrapper}>
              <div style={styles.statusWrapper}>
                <p style={styles.statusText}>録音ステータス: <strong>{status}</strong></p>
              </div>

              <div style={styles.buttonsWrapper}>
                <button style={styles.recordButton} onClick={startRecording}>
                  録音開始
                </button>
                <button style={styles.stopButton} onClick={stopRecording}>
                  録音停止・議事録生成
                </button>
              </div>

              <div style={styles.card}>
                <h2 style={styles.cardTitle}>AIによる議事録と改善案</h2>
                <p style={styles.cardContent}>{summary}</p>
              </div>

              <div style={styles.mindmapContainer}>
                {mindmapData && (
                  <Tree
                    data={mindmapData}
                    orientation="vertical"
                    translate={{ x: 500, y: 50 }}
                    pathFunc="diagonal"
                    separation={{ siblings: 1.5, nonSiblings: 2 }}
                    nodeSize={{ x: 300, y: 100 }}
                  />
                )}
              </div>

              <div style={styles.card}>
                <h2 style={styles.cardTitle}>整形された文字起こし</h2>
                <p style={styles.cardContent}>{notes}</p>
              </div>

              {/* タイトル入力フィールド追加 */}
              <div style={styles.titleInputWrapper}>
                <input
                  type="text"
                  placeholder="議事録のタイトルを入力"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  style={styles.titleInput}
                />
              </div>

              <div style={styles.buttonsWrapper}>
                <button style={styles.saveButton} onClick={saveToDatabase}>
                  議事録を保存する
                </button>
              </div>
            </div>
          )}
        />
      </div>
    </div>
  );
}

const styles = {
  // 既存のスタイルは省略なしで同じものを使用
  appContainer: { backgroundColor: '#f5f5f5', minHeight: '100vh', fontFamily: 'Roboto, Arial, sans-serif', margin: 0, padding: '20px' },
  header: { textAlign: 'center', marginBottom: '20px' },
  title: { fontSize: '2rem', margin: 0, color: '#4285F4' },
  recorderContainer: { maxWidth: '1200px', margin: '0 auto', backgroundColor: '#fff', borderRadius: '8px', boxShadow: '0 2px 5px rgba(0,0,0,0.15)', padding: '20px' },
  controlsWrapper: { display: 'flex', flexDirection: 'column', gap: '20px' },
  statusWrapper: { textAlign: 'center' },
  statusText: { margin: 0, fontSize: '1rem' },
  buttonsWrapper: { display: 'flex', justifyContent: 'center', gap: '10px' },
  recordButton: { backgroundColor: '#34A853', color: '#fff', border: 'none', borderRadius: '4px', padding: '10px 20px', cursor: 'pointer', fontSize: '1rem', boxShadow: '0 1px 4px rgba(0,0,0,0.2)' },
  stopButton: { backgroundColor: '#EA4335', color: '#fff', border: 'none', borderRadius: '4px', padding: '10px 20px', cursor: 'pointer', fontSize: '1rem', boxShadow: '0 1px 4px rgba(0,0,0,0.2)' },
  saveButton: { backgroundColor: '#4285F4', color: '#fff', border: 'none', borderRadius: '4px', padding: '10px 20px', cursor: 'pointer', fontSize: '1rem' },
  closeButton: { marginTop: '20px', padding: '10px 20px', backgroundColor: '#ccc', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '1rem' },
  titleInputWrapper: { textAlign: 'center', margin: '10px 0' },
  titleInput: { padding: '8px', fontSize: '1rem', width: '60%', borderRadius: '5px', border: '1px solid #ccc' },
};

export default App;
