import React, { useState } from 'react';

function App() {
  // 仮の議事録表示用ステート
  const [notes, setNotes] = useState('ここに議事録（仮）を表示します');

  // 録音ボタンが押されたときの仮動作
  const startRecording = () => {
    alert('録音を開始します（仮）');
  };

  const stopRecording = () => {
    alert('録音を停止します（仮）');
  };

  // AI提案ボタンが押されたときの仮動作
  const getAIProposal = () => {
    alert('AI提案を取得します（仮）');
  };

  // マインドマップ表示ボタンが押されたときの仮動作
  const showMindMap = () => {
    alert('マインドマップを表示します（仮）');
  };

  return (
    <div style={{ margin: '20px' }}>
      <h1>会議AIツール（仮）</h1>

      {/* 録音ボタン */}
      <button onClick={startRecording}>録音開始</button>
      <button onClick={stopRecording}>録音停止</button>

      {/* 議事録表示スペース */}
      <div
        style={{
          border: '1px solid #ccc',
          padding: '10px',
          marginTop: '20px',
          minHeight: '100px',
        }}
      >
        <p>{notes}</p>
      </div>

      {/* AI提案ボタン・マインドマップボタン */}
      <div style={{ marginTop: '20px' }}>
        <button onClick={getAIProposal}>AI提案を取得</button>
        <button onClick={showMindMap}>マインドマップを見る</button>
      </div>
    </div>
  );
}

export default App;
