import React, { useState } from 'react';
import { ReactMediaRecorder } from 'react-media-recorder';

function App() {
  const [notes, setNotes] = useState('ここに議事録を表示します');

  // サーバーに音声ファイル(webm)を送信する関数
  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm'); // ファイル名は適当でもOK

    try {
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      setNotes(data.transcript);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h1>会議AIツール</h1>

      <ReactMediaRecorder
        audio
        render={({
          status,
          startRecording,
          stopRecording,
          mediaBlobUrl,
          clearBlobUrl,
        }) => (
          <div>
            <p>録音ステータス: {status}</p>
            <button onClick={startRecording}>録音開始</button>
            <button
              onClick={() => {
                // 録音停止
                stopRecording();
                // 停止が完了するまでmediaBlobUrlが生成されない可能性があるため、少し待機するなどの工夫が必要かも
                // 今回はシンプルにfetch→blob化→送信
                fetch(mediaBlobUrl)
                  .then(res => res.blob())
                  .then(blob => {
                    sendAudioToServer(blob);
                    clearBlobUrl(); // メモリ解放
                  });
              }}
            >
              録音停止・送信
            </button>

            {/* 議事録表示スペース */}
            <div
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginTop: '20px',
                minHeight: '100px',
              }}
            >
              <p>{notes}</p>
            </div>

            {/* 録音内容をブラウザ上で再生確認 */}
            {mediaBlobUrl && (
              <audio src={mediaBlobUrl} controls style={{ marginTop: '20px' }} />
            )}
          </div>
        )}
      />
    </div>
  );
}

export default App;
