import React, { useState } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';

function App() {
  // 議事録表示用ステート
  const [notes, setNotes] = useState('ここに議事録を表示します');

  // 録音関連
  const [isRecording, setIsRecording] = useState(false);
  const recorder = new MicRecorder({ bitRate: 128 }); // MP3ビットレート

  // 音声ファイルをサーバーに送信して文字起こし
  const sendAudioToServer = async (file) => {
    const formData = new FormData();
    formData.append('audio', file);

    try {
      // ここでバックエンドの文字起こしAPIを呼ぶ
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      // data.transcript が文字起こし結果として返ってくる想定
      setNotes(data.transcript);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    }
  };

  // 録音開始ボタン
  const startRecording = () => {
    recorder
      .start()
      .then(() => {
        setIsRecording(true);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  // 録音停止ボタン
  const stopRecording = () => {
    recorder
      .stop()
      .getMp3()
      .then(async ([buffer, blob]) => {
        setIsRecording(false);
        // blobをファイルとしてサーバーに送信
        const file = new File(buffer, 'recording.mp3', { type: blob.type });
        await sendAudioToServer(file);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  // AI提案ボタン（仮）
  const getAIProposal = () => {
    alert('AI提案を取得します（仮）');
  };

  // マインドマップ表示ボタン（仮）
  const showMindMap = () => {
    alert('マインドマップを表示します（仮）');
  };

  return (
    <div style={{ margin: '20px' }}>
      <h1>会議AIツール</h1>

      {/* 録音ボタン */}
      <button onClick={startRecording} disabled={isRecording}>
        録音開始
      </button>
      <button onClick={stopRecording} disabled={!isRecording}>
        録音停止
      </button>

      {/* 議事録表示スペース */}
      <div
        style={{
          border: '1px solid #ccc',
          padding: '10px',
          marginTop: '20px',
          minHeight: '100px',
        }}
      >
        <p>{notes}</p>
      </div>

      {/* AI提案ボタン・マインドマップボタン */}
      <div style={{ marginTop: '20px' }}>
        <button onClick={getAIProposal}>AI提案を取得</button>
        <button onClick={showMindMap}>マインドマップを見る</button>
      </div>
    </div>
  );
}

export default App;
