import React, { useState, useEffect } from 'react';
import Tree from 'react-d3-tree';
import { ReactMediaRecorder } from 'react-media-recorder';

function App() {
  // ステート定義
  const [notes, setNotes] = useState('ここに整形された文字起こしが表示されます');
  const [summary, setSummary] = useState('ここに議事録・改善案が表示されます');
  const [mindmapData, setMindmapData] = useState(null);
  const [title, setTitle] = useState('');
  const [recording, setRecording] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);
  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // 保存済み議事録をAPIから取得（直近のものが上に来るようにソート）
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch('http://localhost:8000/get-minutes');
      if (!res.ok) throw new Error('保存された議事録の取得に失敗しました');
      const data = await res.json();
      // data.minutes が配列の場合、直近のものが上になるようソート（idが数値の場合の例）
      const sorted = data.minutes.sort((a, b) => b.id - a.id);
      setSavedMinutes(sorted);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  // 音声をサーバーへ送信して、文字起こし・議事録データを取得
  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm');
    try {
      setLoading(true);
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });
      if (!res.ok) throw new Error(`サーバーエラー: ${res.statusText}`);
      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  // 議事録保存処理
  const saveToDatabase = async () => {
    if (!title.trim()) {
      alert("タイトルを入力してください。");
      return;
    }
    try {
      const res = await fetch('http://localhost:8000/save-minutes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          formatted_transcript: notes,
          analysis: summary,
          mindmap: mindmapData,
          title: title,
        }),
      });
      if (!res.ok) throw new Error('保存に失敗しました');
      const result = await res.json();
      if (result.status === 'success') {
        setSaveStatus('保存が完了しました！');
        fetchSavedMinutes(); // 保存後、一覧を更新
      } else {
        setSaveStatus('保存に失敗しました');
      }
    } catch (err) {
      console.error(err);
      setSaveStatus('保存中にエラーが発生しました');
    }
  };

  const closeSaveModal = () => {
    setSaveStatus(null);
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div style={styles.appContainer}>
      {/* キーアニメーションの定義 */}
      <style>{`
        @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
        @keyframes slideDown { from { transform: translateY(-20px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
        @keyframes popIn { from { transform: scale(0.9); opacity: 0; } to { transform: scale(1); opacity: 1; } }
      `}</style>

      {/* ヘッダー */}
      <header style={styles.header}>
        <div style={styles.headerLeft}>
          <button style={styles.toggleButton} onClick={toggleSidebar}>
            {sidebarOpen ? '←' : '→'}
          </button>
          <h1 style={styles.logo}>Conect AI</h1>
        </div>
        <div style={styles.headerRight}>
          {/* ナビゲーションボタン（必要に応じて追加） */}
        </div>
      </header>

      {/* メインレイアウト：サイドバー＋メインコンテンツ */}
      <div style={styles.contentWrapper}>
        {sidebarOpen && (
          <aside style={styles.sidebar}>
            <h2 style={styles.sidebarTitle}>保存した議事録</h2>
            {savedMinutes.length > 0 ? (
              savedMinutes.map((minute) => (
                <div
                  key={minute.id}
                  style={styles.sidebarItem}
                  onClick={() => setSelectedMinute(minute)}
                >
                  {minute.title}
                </div>
              ))
            ) : (
              <p style={styles.sidebarText}>保存された議事録はありません</p>
            )}
          </aside>
        )}
        <main style={{ ...styles.mainContent, marginLeft: sidebarOpen ? 300 : 20 }}>
          {selectedMinute ? (
            // 保存済み議事録詳細表示
            <div style={styles.detailContainer}>
              <button style={styles.backButton} onClick={() => setSelectedMinute(null)}>戻る</button>
              <h2 style={styles.detailTitle}>{selectedMinute.title}</h2>
              <div style={styles.card}>
                <h3 style={styles.cardTitle}>整形された文字起こし</h3>
                <p style={styles.cardContent}>{selectedMinute.formatted_transcript}</p>
              </div>
              <div style={styles.card}>
                <h3 style={styles.cardTitle}>議事録・改善案</h3>
                <p style={styles.cardContent}>{selectedMinute.analysis}</p>
              </div>
              {selectedMinute.mindmap && (
                <div style={styles.mindmapContainer}>
                  <Tree
                    data={selectedMinute.mindmap}
                    orientation="vertical"
                    translate={{ x: 400, y: 50 }}
                    pathFunc="diagonal"
                    separation={{ siblings: 1.5, nonSiblings: 2 }}
                    nodeSize={{ x: 300, y: 100 }}
                  />
                </div>
              )}
            </div>
          ) : (
            // 録音＆議事録作成UI
            <div style={styles.recorderContainer}>
              {/* アプリの説明文 */}
              <div style={styles.descriptionBox}>
                <p style={styles.descriptionText}>
                  このアプリでは、会議の録音、文字起こし、議事録・改善案生成、そしてマインドマップ作成が可能です。<br />
                  録音開始ボタンを押して、会議内容を自動解析し、後から確認・保存できます。
                </p>
              </div>
              <ReactMediaRecorder
                audio
                video={false}
                mimeType="audio/webm;codecs=opus"
                onStop={(blobUrl, blob) => {
                  sendAudioToServer(blob);
                }}
                render={({ status, startRecording, stopRecording }) => (
                  <div style={styles.controlsWrapper}>
                    {/* 初期状態：録音開始ボタンのみ表示 */}
                    {!recording && (
                      <button
                        style={styles.actionButton}
                        onClick={() => {
                          startRecording();
                          setRecording(true);
                        }}
                      >
                        録音開始
                      </button>
                    )}
                    {/* 録音中：録音開始ボタンを隠し、議事録作成ボタンを表示 */}
                    {recording && !showModal && (
                      <button
                        style={styles.actionButton}
                        onClick={() => {
                          stopRecording();
                          setShowModal(true);
                          setRecording(false);
                        }}
                      >
                        議事録作成
                      </button>
                    )}
                  </div>
                )}
              />

              {/* 議事録作成モーダル */}
              {showModal && (
                <div style={styles.modalOverlay}>
                  <div style={styles.modalContent}>
                    <h2 style={styles.modalTitle}>議事録作成</h2>
                    <input
                      type="text"
                      placeholder="タイトルを入力"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                      style={styles.titleInput}
                    />
                    <div style={styles.modalSection}>
                      <h3 style={styles.modalSubTitle}>整形された文字起こし</h3>
                      <p style={styles.modalText}>{notes}</p>
                    </div>
                    <div style={styles.modalSection}>
                      <h3 style={styles.modalSubTitle}>議事録・改善案</h3>
                      <p style={styles.modalText}>{summary}</p>
                    </div>
                    <button style={styles.modalActionButton} onClick={saveToDatabase}>
                      議事録を保存する
                    </button>
                    <button style={styles.modalCloseButton} onClick={() => setShowModal(false)}>
                      閉じる
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </main>
      </div>

      {/* ローディングモーダル */}
      {loading && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={styles.modalText}>議事録を生成中です...お待ちください</p>
          </div>
        </div>
      )}

      {/* 保存結果モーダル */}
      {saveStatus && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={styles.modalText}>{saveStatus}</p>
            <button style={styles.modalCloseButton} onClick={closeSaveModal}>
              閉じる
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

// スタイル定義（アニメーション・トランジションを多用し、スタイリッシュな動きを実現）
const styles = {
  appContainer: {
    backgroundColor: '#fff',
    minHeight: '100vh',
    fontFamily: '"Montserrat", "Playfair Display", sans-serif',
    color: '#333',
    padding: '20px',
    transition: 'all 0.3s ease',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: '10px 20px',
    borderBottom: '1px solid #ddd',
    position: 'sticky',
    top: 0,
    zIndex: 10,
  },
