import React, { useState, useEffect } from 'react';
import Tree from 'react-d3-tree';

function App() {
  const [notes, setNotes] = useState('ここに整形された文字起こしを表示します');
  const [summary, setSummary] = useState('ここに議事録と改善案が表示されます');
  const [mindmapData, setMindmapData] = useState(null);

  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm');

    try {
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    }
  };

  return (
    <div style={{ margin: '20px', fontFamily: 'Arial' }}>
      <h1>会議AIツール</h1>

      <ReactMediaRecorder
        audio
        video={false}
        mimeType="audio/webm;codecs=opus"
        onStop={(blobUrl, blob) => {
          sendAudioToServer(blob);
        }}
        render={({ status, startRecording, stopRecording }) => (
          <div>
            <p>録音ステータス: {status}</p>

            <button onClick={startRecording}>
              録音開始
            </button>

            <button onClick={stopRecording}>
              録音停止・議事録生成
            </button>

            {/* AIによる議事録と改善案表示エリア */}
            <div style={{
              border: '1px solid #ccc',
              padding: '10px',
              marginTop: '20px',
              minHeight: '80px',
              borderRadius: '5px'
            }}>
              <h2>AIによる議事録と改善案</h2>
              <p style={{ whiteSpace: 'pre-wrap' }}>{summary}</p>
            </div>

            {/* マインドマップ表示エリア */}
            <div style={{
              width: '100%',
              height: '500px',
              border: '1px solid #ccc',
              borderRadius: '5px',
              marginTop: '20px'
            }}>
              {mindmapData && (
                <Tree
                  data={mindmapData}
                  orientation="vertical"
                  translate={{ x: 500, y: 50 }}
                  pathFunc="diagonal"
                  separation={{ siblings: 1.5, nonSiblings: 2 }}
                  nodeSize={{ x: 300, y: 100 }}
                />
              )}
            </div>

            {/* 整形された文字起こし表示エリア */}
            <div style={{
              border: '1px solid #ccc',
              padding: '10px',
              marginTop: '20px',
              minHeight: '80px',
              borderRadius: '5px'
            }}>
              <h2>整形された文字起こし</h2>
              <p style={{ whiteSpace: 'pre-wrap' }}>{notes}</p>
            </div>
          </div>
        )}
      />
    </div>
  );
}

// ReactMediaRecorderをimport
import { ReactMediaRecorder } from 'react-media-recorder';

export default App;
