import React, { useState } from 'react';
import { ReactMediaRecorder } from 'react-media-recorder';

function App() {
  const [notes, setNotes] = useState('ここに議事録を表示します');

  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm');

    try {
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      setNotes(data.transcript);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h1>会議AIツール</h1>

      <ReactMediaRecorder
        audio
        video={false}
        mimeType="audio/webm;codecs=opus"
        render={({
          status,
          startRecording,
          stopRecording,
          mediaBlobUrl,
          clearBlobUrl,
        }) => (
          <div>
            <p>録音ステータス: {status}</p>

            {/* 録音開始 */}
            <button onClick={startRecording}>
              録音開始
            </button>

            {/* 録音停止・送信 */}
            <button
              onClick={async () => {
                stopRecording();
                // 録音停止後にblobの生成を待機してから送信
                const waitForBlob = async () => {
                  while (!mediaBlobUrl) {
                    await new Promise(resolve => setTimeout(resolve, 100));
                  }
                };
                await waitForBlob();
                fetch(mediaBlobUrl)
                  .then(res => res.blob())
                  .then(blob => {
                    sendAudioToServer(blob);
                    clearBlobUrl();
                  });
              }}
            >
              録音停止・送信
            </button>

            {/* 議事録表示エリア */}
            <div
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginTop: '20px',
                minHeight: '100px',
              }}
            >
              <p>{notes}</p>
            </div>

            {/* 録音データの再生確認用 */}
            {mediaBlobUrl && (
              <audio src={mediaBlobUrl} controls style={{ marginTop: '20px' }} />
            )}
          </div>
        )}
      />
    </div>
  );
}

export default App;
