import React, { useState, useEffect } from 'react';
import { ReactMediaRecorder } from 'react-media-recorder';
import Tree from 'react-d3-tree';

function App() {
  const [notes, setNotes] = useState('ここに議事録を表示します');
  const [mindMapData, setMindMapData] = useState(null);
  const [formattedTranscript, setFormattedTranscript] = useState('');

  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm');

    try {
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      setNotes(data.analysis);
      setMindMapData(data.mindmap);
      setFormattedTranscript(data.formatted_transcript);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h1>会議AIツール</h1>

      <ReactMediaRecorder
        audio
        video={false}
        mimeType="audio/webm;codecs=opus"
        onStop={(blobUrl, blob) => sendAudioToServer(blob)}
        render={({ status, startRecording, stopRecording }) => (
          <div>
            <p>録音ステータス: {status}</p>

            <button onClick={startRecording}>録音開始</button>
            <button onClick={stopRecording}>録音停止</button>

            <div
              style={{
                border: '1px solid #ccc',
                padding: '10px',
                marginTop: '20px',
                minHeight: '100px',
              }}
            >
              <h2>AIによる議事録と改善案</h2>
              <p>{notes}</p>
            </div>

            {mindMapData && (
              <div
                style={{
                  height: '400px',
                  marginTop: '30px',
                  border: '1px solid #ccc',
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                }}
              >
                <Tree
                  data={mindMapData}
                  orientation="vertical"
                  translate={{ x: 300, y: 50 }}
                  pathFunc="elbow"
                  separation={{ siblings: 2, nonSiblings: 3 }}
                  nodeSize={{ x: 250, y: 100 }}
                  styles={{
                    nodes: {
                      node: { circle: { fill: '#888', r: 6 }, name: { fontSize: '13px', padding: '5px' } },
                      leafNode: { circle: { fill: '#FFF', stroke: '#888', r: 6 }, name: { fontSize: '13px', padding: '5px' } },
                    },
                  }}
                />
              </div>
            )}

            {formattedTranscript && (
              <div
                style={{
                  marginTop: '30px',
                  padding: '10px',
                  border: '1px solid #ccc',
                  backgroundColor: '#f9f9f9',
                }}
              >
                <h3>整形された文字起こし</h3>
                <p>{formattedTranscript}</p>
              </div>
            )}
          </div>
        )}
      />
    </div>
  );
}

export default App;
