import React, { useState, useEffect } from 'react';
import Tree from 'react-d3-tree';
import { ReactMediaRecorder } from 'react-media-recorder';

function App() {
  const [notes, setNotes] = useState('ここに整形された文字起こしを表示します');
  const [summary, setSummary] = useState('ここに議事録と改善案が表示されます');
  const [mindmapData, setMindmapData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [saveStatus, setSaveStatus] = useState(null);
  const [title, setTitle] = useState('');
  const [savedMinutes, setSavedMinutes] = useState([]);
  const [selectedMinute, setSelectedMinute] = useState(null);

  // 録音結果をサーバーに送信
  const sendAudioToServer = async (audioBlob) => {
    const formData = new FormData();
    formData.append('audio', audioBlob, 'recording.webm');

    try {
      setLoading(true);
      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) {
        throw new Error(`サーバーエラー: ${res.statusText}`);
      }

      const data = await res.json();
      setNotes(data.formatted_transcript);
      setSummary(data.analysis);
      setMindmapData(data.mindmap);
    } catch (error) {
      console.error('通信エラー:', error);
      setNotes('通信エラーが発生しました');
    } finally {
      setLoading(false);
    }
  };

  // 保存済み議事録を取得する関数
  const fetchSavedMinutes = async () => {
    try {
      const res = await fetch('http://localhost:8000/get-minutes');
      if (!res.ok) throw new Error('保存された議事録の取得に失敗しました');
      const data = await res.json();
      // data.minutes を返す想定
      setSavedMinutes(data.minutes);
    } catch (error) {
      console.error('Error fetching saved minutes:', error);
    }
  };

  useEffect(() => {
    fetchSavedMinutes();
  }, []);

  // 議事録を保存する処理
  const saveToDatabase = async () => {
    if (!title.trim()) {
      alert("タイトルを入力してください。");
      return;
    }

    try {
      const res = await fetch('http://localhost:8000/save-minutes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          formatted_transcript: notes,
          analysis: summary,
          mindmap: mindmapData,
          title: title
        }),
      });

      if (!res.ok) throw new Error('保存に失敗しました');

      const result = await res.json();
      if (result.status === 'success') {
        setSaveStatus('保存が完了しました！');
        fetchSavedMinutes(); // 保存後、一覧を更新
      } else {
        setSaveStatus('保存に失敗しました');
      }
    } catch (err) {
      console.error(err);
      setSaveStatus('保存中にエラーが発生しました');
    }
  };

  const closeSaveModal = () => {
    setSaveStatus(null);
  };

  return (
    <div style={styles.appContainer}>
      <header style={styles.header}>
        <h1 style={styles.title}>会議AIツール</h1>
      </header>

      {loading && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={{ fontSize: '18px', color: '#333' }}>議事録を生成中です...お待ちください</p>
          </div>
        </div>
      )}

      {saveStatus && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <p style={{ fontSize: '18px', color: '#333' }}>{saveStatus}</p>
            <button style={styles.closeButton} onClick={closeSaveModal}>閉じる</button>
          </div>
        </div>
      )}

      {/* サイドバーとメインエリアのレイアウト */}
      <div style={styles.contentContainer}>
        {/* サイドバー */}
        <aside style={styles.sidebar}>
          <h2 style={styles.sidebarTitle}>保存した議事録</h2>
          {savedMinutes.length > 0 ? (
            savedMinutes.map((minute) => (
              <div
                key={minute.id}
                style={styles.sidebarItem}
                onClick={() => setSelectedMinute(minute)}
              >
                {minute.title}
              </div>
            ))
          ) : (
            <p>保存された議事録はありません</p>
          )}
        </aside>

        {/* メインコンテンツエリア */}
        <main style={styles.mainContent}>
          {selectedMinute ? (
            // 保存済み議事録詳細表示
            <div>
              <button style={styles.backButton} onClick={() => setSelectedMinute(null)}>戻る</button>
              <h2>{selectedMinute.title}</h2>
              <div style={styles.card}>
                <h3 style={styles.cardTitle}>整形された文字起こし</h3>
                <p style={styles.cardContent}>{selectedMinute.formatted_transcript}</p>
              </div>
              <div style={styles.card}>
                <h3 style={styles.cardTitle}>議事録と改善案</h3>
                <p style={styles.cardContent}>{selectedMinute.analysis}</p>
              </div>
              {selectedMinute.mindmap && (
                <div style={styles.mindmapContainer}>
                  <Tree
                    data={selectedMinute.mindmap}
                    orientation="vertical"
                    translate={{ x: 500, y: 50 }}
                    pathFunc="diagonal"
                    separation={{ siblings: 1.5, nonSiblings: 2 }}
                    nodeSize={{ x: 300, y: 100 }}
                  />
                </div>
              )}
            </div>
          ) : (
            // 録音・議事録生成UI
            <div style={styles.recorderContainer}>
              <ReactMediaRecorder
                audio
                video={false}
                mimeType="audio/webm;codecs=opus"
                onStop={(blobUrl, blob) => sendAudioToServer(blob)}
                render={({ status, startRecording, stopRecording }) => (
                  <div style={styles.controlsWrapper}>
                    <div style={styles.statusWrapper}>
                      <p style={styles.statusText}>
                        録音ステータス: <strong>{status}</strong>
                      </p>
                    </div>

                    <div style={styles.buttonsWrapper}>
                      <button style={styles.recordButton} onClick={startRecording}>
                        録音開始
                      </button>
                      <button style={styles.stopButton} onClick={stopRecording}>
                        録音停止・議事録生成
                      </button>
                    </div>

                    <div style={styles.card}>
                      <h2 style={styles.cardTitle}>AIによる議事録と改善案</h2>
                      <p style={styles.cardContent}>{summary}</p>
                    </div>

                    <div style={styles.mindmapContainer}>
                      {mindmapData && (
                        <Tree
                          data={mindmapData}
                          orientation="vertical"
                          translate={{ x: 500, y: 50 }}
                          pathFunc="diagonal"
                          separation={{ siblings: 1.5, nonSiblings: 2 }}
                          nodeSize={{ x: 300, y: 100 }}
                        />
                      )}
                    </div>

                    <div style={styles.card}>
                      <h2 style={styles.cardTitle}>整形された文字起こし</h2>
                      <p style={styles.cardContent}>{notes}</p>
                    </div>

                    <div style={styles.titleInputWrapper}>
                      <input
                        type="text"
                        placeholder="議事録のタイトルを入力"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        style={styles.titleInput}
                      />
                    </div>

                    <div style={styles.buttonsWrapper}>
                      <button style={styles.saveButton} onClick={saveToDatabase}>
                        議事録を保存する
                      </button>
                    </div>
                  </div>
                )}
              />
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

